import React from 'react'
import '../styles/noteCard.css'

const NoteCard = ({note, onDelete}) => {
  return (
    <div className='note-card'>
        <h3>{note.title}</h3>
        <p>{note.content}</p>
        <button onClick={() => onDelete(note.id)}>X</button>
    </div>
  )
}

export default NoteCard
